$(function(){
    settime();
    //$.getJSON("http://www.wangnafei.com/flight/flight_list.json",function(result){
        flightselect(flight_result);        
    //});
    var startDate = new Date(new Date().getTime()+3*86400000);
    $(".departure_day,.return_day").datetimepicker({
        format: 'yyyy-mm-dd',
        minView:2,
        startDate:startDate,
        autoclose:true
    }); 
	var $this = $(".selectIcon");
    $(".selectIcon").focus(function() {
        $this = $(this);
        var $width = $(this).outerWidth(true);
        var $height = $(this).outerHeight(true);
        var $top = $(this).position().top;
        var $left = $(this).position().left;

        $this.next("div.selectTip").show().css({
            "width":$width,
            "left":$left,
            "top":$top+$height-1
        });
        //$(options).next("div.selectTip").hide();
        $(".selectTip").hide();
        $this.next("div.selectTip").show().css({
            "width":$width,
            "left":$left,
            "top":$top+$height-1
        });
    });

    $(".selectIcon").next("div.selectTip").children('a').click(function(){
        var $val = $(this).text();
        var $data = $(this).attr('data');
        $this.val($val);
        if($data){
           var hiddenvar = $this.attr('hiddenvar'); 
           $("#"+hiddenvar).val($data);
        }
        $("this").parent().hide();
    });

    $(document).bind("click", function (e) {
        if($(e.target).closest($this).size()){
            $this.siblings("div.selectTip").show();
        }else{
            $this.siblings("div.selectTip").hide();
        }
    });
    $(".flightselectbutton").click(function(event) {
        a = $(this).parents('.in-bo').find(".flightselect");
        $('.flightselect').removeClass('current');      
        a.addClass('current');
        $('#flightselect').modal({'show':true,backdrop:false});  
    });
    $(".cityselectbutton").click(function(event) {
        a = $(this).parents('.form-group').find(".cityselect");
        cityselect(a.attr('datatype'));
        $(".cityselect").removeClass('current');      
        a.addClass('current');
        $('#cityselect').modal({'show':true,backdrop:false});
    });
});



//机票选择按钮
function selectTab_jp(q,id) {
    $("#changTab li").removeClass('cur');
    id.parent("li").addClass('cur');
    $("#TRIP_TYPE").attr("value",q);
    if(q=="O"){
        $("#moreflight").hide();
        $("#oneflight").show();
        $(".onlyone").hide();        
    }
    else if(q=="M"){
        $("#oneflight").hide();
        $(".onlyone").hide();
        $("#moreflight").show();
    }
    else{
        $("#moreflight").hide();
        $("#oneflight").show();
        $(".onlyone").show();    
    }
    iframe_height();
}

