function flightselect(data){
    $.each(data, function(i, field){
        var html = '<div class="container mt10" style="height:400px; overflow-y:auto;">';
        $.each(field, function(k, v){
            html +='<div class="col-sm-4 flightselecthtml"><a href="javascript:;" onclick="selectflight(\''+v.id+'\',\''+v.description+'\');">'+v.description+'</a></div>';
        })
        html += '</div>';
        $("#"+i+'_FStab').html(html);
    });
}

function selectflight(id,name){
    $(".flightselect.current").val(id);
    $(".flightselect.current").tooltip('destroy');
    $(".flightselect.current").tooltip({'show':true,title:name,placement:'auto'});
    $('#flightselect').modal('hide');
}

function cityselect(type){
    var city = type==1 ? city1 : city2;
    var html = '<div class="container">';
    $.each(city, function(i, field){        
        html +='<div class="col-sm-4 flightselecthtml"><a href="javascript:;" onclick="selectcity(\''+i+'\',\''+field+'\');">'+field+' ('+i+')</a></div>';
    });
    html += '</div>';   
    $("#cityselect .modal-body").html(html);
}

function selectcity(id,name){
    $(".cityselect.current").val(id);
    $(".cityselect.current").tooltip('destroy');
    $(".cityselect.current").tooltip({'show':true,title:name,placement:'auto'});
    $('#cityselect').modal('hide');
}

function jpCheck(){
    var numbernum = parseInt(selectinputname("FIELD_ADT_NUMBER").val())+parseInt(selectinputname("FIELD_CHD_NUMBER").val())+parseInt(selectinputname("FIELD_INFANTS_NUMBER").val())
    if(numbernum>9){
        seterror("可得性查询请求的最大乘客数为 9。 请修改请求乘客数并重试。","旅客数量","FIELD_ADT_NUMBER_1");
        return false;   
    }
    if(numbernum<1){
        seterror("请提供一个以上的旅客。","旅客数量","FIELD_ADT_NUMBER_1");
        return false;   
    }
    if(parseInt(selectinputname("FIELD_ADT_NUMBER").val())==0){
        seterror("婴儿或者儿童不能独自出行而必须由成年旅客陪伴出行","旅客数量","FIELD_ADT_NUMBER_1");
        return false;   
    }   
    if(parseInt(selectinputname("FIELD_ADT_NUMBER").val())<parseInt(selectinputname("FIELD_INFANTS_NUMBER").val())){
        seterror("每位成人只能携带一位婴儿旅客。","旅客数量","FIELD_ADT_NUMBER_1");
        return false;   
    }
    if($("#TRIP_TYPE").val()=="R" || $("#TRIP_TYPE").val()=="O"){         
        if(!selectinputname("B_LOCATION_1").val()){
            seterror("Please provide the originating city or airport code. Please add and try again.","To city or airport","B_LOCATION_1");
            return false;   
        }
        if(!selectinputname("E_LOCATION_1").val()){
            seterror("请提供目的城市或机场代码。请添加并重试。","目的城市","E_LOCATION_1");
            return false;   
        }
        if(selectinputname("B_LOCATION_1").val()==selectinputname("E_LOCATION_1").val()){
            seterror("您的启程城市和到达城市相同。请修改您的请求并重试。","始发城市和目的城市","B_LOCATION_1");
            return false;   
        }
        if(!timeduibi("current",selectinputname("setdeparture_day_1").val())){
            seterror("您的启程日期在当前日期以前或等于当前日期，请修改后重试","日期","settime_1");
            return false;
        }
        if($("#TRIP_TYPE").val()=="R" && !timeduibi(selectinputname("setdeparture_day_1").val(),selectinputname("setreturn_day_1").val())){
            seterror("返程日期/时间同于或早于启程日期/时间。请调整有关日期/时间并重试。","日期","settime_2");
            return false;
        }        
    }
    else if($("#TRIP_TYPE").val()=="M"){}
    return true;     
}
function selectinputname(name){
    return $('input[name='+name+']');
}
function timeduibi(time1,time2){
    if(time1=="current"){     
       time1=new Date().getFullYear()+"-"+(new Date().getMonth()+1)+"-"+new Date().getDate();   
    }
    if(time2==""){
        time2=new Date().getFullYear()+"-"+(new Date().getMonth()+1)+"-"+new Date().getDate();  
    }
       var fs=time1.split("-");
       var ss=time2.split("-");
       var t1=new Date(fs[0],fs[1]-1,fs[2]);
       var t2=new Date(ss[0],ss[1]-1,ss[2]);
        if(t2-t1>=0)
        return true;
        else 
        return false;   
}

function seterror(str,name,id){
    var html = '<div class="alert alert-danger mb0 alert-dismissable" errorid="'+id+'">';
    html += '<button type="button" class="close" data-dismiss="alert" aria-hidden="true">&times;</button>';
    html += '<strong>['+name+'] Error!</strong> '+str+'</div>';
    $(".search_content").prepend(html);
}

